INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (1, 1, 6, 'Ail', 'Ail blanc avec 3 têtes pour parfumer tous vos plats. Origine / Calibre: 250 gr - France - Cal 50/70 - Catégorie : 1', 'ail.jpg', 'ail125px.jpg', '0.250', '1.99', '7.96');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (2, 2, 3, 'Carottes', 'La carotte est un légume qui se consomme en toutes saisons. Délicieuse, elle est riche en bêta-carotène, en provitamine A, en minéraux et pauvre en calories.  Origine / Calibre : 1 kg - France - Cal 20/40 - Catégorie : 2', 'carotte.jpg', 'carotte125px.jpg', '1.00', '2.88', '2.88');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (3, 3, 3, 'Celeri Rave', 'Ingrédient de base du fameux céleri rémoulade, sa chair parfumée donne aussi de délicieux gratins ou purées savoureuses. Riche en fibres, il comporte du potassium, du calcium, du fer, des oligoéléments.  Origine / Calibre : 750 gr - France - Catégorie : 1', 'celeri.jpg', 'celeri125px.jpg' , '0.75', '2.99', '3.99');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (4, 4, 3, 'Chou Rouge', 'Ce chou rouge Pomona se déguste aussi bien chaud que froid. Riche en fibres et minéraux, le chou est un allié de taille contre la fatigue et possède de nombreuses propriétés antioxydantes. Origine / Calibre : 1.3 kg env - France - Cal 6 - Catégorie : 1', 'chouRouge.jpg', 'chouRouge125px.jpg' , '1.3', '4.28', '2.99');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (5, 5, 3, 'Chou Vert', 'Légume incontournable de la cuisine rustique, le chou vert peut être cuisiné de multiples façons.  Origine / Calibre : 1.3 kg - France - Cal 6 - Catégorie : 1', 'chouVert.jpg', 'chouVert125px.jpg' , '1.3', '3.93', '3.28');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (6, 6, 5, 'Ciboulette fraîche', 'Bouquet de ciboulette fraîche de 20 g pour agrémenter toutes vos recettes. Origine / Calibre : 20 gr - France - Catégorie : 1', 'ciboulette.jpg', 'ciboulette125px.jpg' ,'0.02', '1.44', '72.00');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (7, 7, 2, 'Citron jaune', 'Filet d''environ 500 g de citrons biologiques. Origine / Calibre : 1 filet 500 gr env - France - Catégorie : 1', 'citron.jpg', 'citron125px.jpg' , '0.5', '2.23', '4.46');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (8, 8, 2, 'Clémentine', 'La chair sucrée et douce de la clémentine, la classe première au hit-parade des agrumes. Ouvrez la clémentine en quartiers avec tendresse, avant de la déguster. Origine / Calibre : 1 kg - France - Cal : 2 - Catégorie : 1', 'clementine.jpg', 'clementine125px.jpg' , '0.50', '3.99', '3.99');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (9, 9, 3, 'Courge butternut', 'Préparez une soupe de courge butternut ou un délicieux velouté ou encore une savoureuse purée qui sera doucement sucrée et musquée. Origine / Calibre : 1.2 kg - France - Catégorie : 1', 'courgeButternut.jpg', 'courgeButternut125px.jpg' , '1.00', '3.95', '3.95');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (10, 10, 3, 'Endives', 'L''endive est un des légumes les plus simples à préparer. Crue, cuite, en salade, vous n''avez que l''embarras du choix . Origine / Calibre : 1 kg - France - Catégorie : 1', 'endive.jpg', 'endive125px.jpg' , '1.00', '2.97', '2.97');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (11, 11, 2, 'Kiwis', 'Originaire de Chine, puis implanté en Nouvelle-Zélande, ce fruit fut implanté en France dans les années 70. Il peut se consommer plus ou moins mûr selon que vous le préfériez fondant et sucré ou bien plus ferme et un rien acidulé. Origine / Calibre : 6 fruits - 500 gr- France - Catégorie : 1', 'kiwi.jpg',  'kiwi125px.jpg' , '0.50', '4.65', '9.30');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (12, 12, 4, 'Laitue iceberg', 'Laitue iceberg pour une salade croquante. Origine / Calibre : 6 fruits - 500 gr - France - Catégorie : 1', 'laitueIceberg.jpg', 'laitueIceberg125px.jpg' , '0.5', '2.50', '5.00');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (13, 13, 5, 'Laurier sec', 'Le composant indispensable du fameux "bouquet garni". Origine / Calibre : 30 gr - France - Catégorie : 2', 'laurier.jpg', 'laurier125px.jpg' , '0.03', '1.57', '52.33');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (14, 14, 3, 'Navet', 'Au même titre que la carotte et le poireau, le navet fait partie de la trilogie des légumes dits basiques. Il est surtout apprécié au printemps pour sa saveur douce de primeur et en hiver pour la préparation de soupes et de pot-au-feu. Origine / Calibre : 500 gr - France - Cal : 40/60 - Catégorie : 1.', 'navet.jpg', 'navet125px.jpg' , '0.50', '2.94', '5.88');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (15, 15, 6, 'Oignon botte', 'L''oignon en botte est un condiment dont l''usage est très répandu en cuisine. Son parfum est plus doux que son cousin l''oignon "blanc". Il est donc conseillé de le déguster cru en salade afin de profiter pleinement de sa saveur. Origine / Calibre : 150 gr - France - Catégorie : 1.', 'oignonBotte.jpg', 'oignonBotte125px.jpg' , '0.15', '2.96', '19.73');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (16, 16, 6, 'Oignon jaune', 'Oignons jaunes issus de l''agriculture biologique, le filet de 500 g. Origine / calibre * : 500 g - France - Cal 40/60 - Catégorie : 2', 'oignonJaune.jpg', 'oignonJaune125px.jpg' , '0.500', '2.23', '4.46');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (17, 17, 6, 'Oignon rouge', 'L''oignon est à la fois un légume et un condiment. Bien que la couleur de sa peau soit bien plus proche du violet que du rouge. L''oignon rouge est légèrement plus sucré que l''oignon jaune, et surtout il s''accorde mieux avec les plats non cuisinés comme les salades, les sandwichs ou hamburgers. Origine / calibre * : 500 gr - France - Cal 50/70 - Catégorie : 1', 'oignonRouge.jpg', 'oignonRouge125px.jpg' , '0.500', '3.64', '7.28');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (18, 18, 2, 'Orange', 'L''orange, est un fruit très apprécié en hiver. Riche en calcium et en vitamine C, il se consomme : en dessert léger, en en-cas rafraîchissant, ou sous forme de jus, et vous séduira par sa saveur finement acidulée. En cuisine, elle a sa place dans des recettes sucrées ou salées. Produit issu de l''Agriculture Biologique (AB). Origine / calibre * : 4 fruits, 650 gr env - France - Cal 7/8 - Catégorie : 2', 'orange.jpg', 'orange125px.jpg' , '0.650', '2.58', '3.97');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (19, 19, 3, 'Panais', 'Le panais est une racine d''un joli blanc ivoire à la saveur douce et au petit goût sucré de noisette. Sa forme est plus ou moins allongée selon les variétés. Le panais était déjà cultivé au Moyen Age car on en a retrouvé des traces dans les registres des plantes cultivées dans les monastères à des fins médicales. Il était aussi bien utilisé en tant que plante fourragère que pour l''alimentation humaine. Il est aujourd''hui un peu délaissé en Europe sauf dans les pays nordiques et en Angleterre qui en consomme davantage. Origine / calibre * : 800 g - France - Catégorie : 1.', 'panais.jpg', 'panais125px.jpg' , '0.800', '4.39', '4.99');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (20, 20, 5, 'Persil frisé', 'L''une des herbes les plus indispensables pour parfumer et orner tous vos plats. Feuillage frisé très fourni. Origine / calibre * : 150 g - France - Catégorie : 1.', 'persilFrise.jpg', 'persilFrise125px.jpg' , '0.150', '2.70', '18.0');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (21, 21, 2, 'Poires Louise', 'Savourez de délicieuses poires issues de nos terroirs français, juteuses et goûteuses, les poires ont de nombreuses vertus. Barquette de 4 fruits soit environ 800 g. Origine / calibre * : 4 fruits, 800 gr env - France - Cal 60/65 - Catégorie : 1', 'poire.jpg', 'poire125px.jpg' , '0.800', '4.31', '4.90');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (22, 22, 3, 'Poireaux entiers', 'Le poireau est très ancien car il est connu depuis l''époque des Egyptiens ! En France, il a été introduit par les Romains, mais le terme poireau n''est apparu que récemment. On le dénommait auparavant porreau. En France, au Moyen Age, le poireau était considéré comme l''aliment de base des gens pauvres. Il existe actuellement une dizaine de variétés qui sont cultivées de septembre à mars. Origine / calibre * : 1 kg - France - Catégorie : 1', 'poireau.jpg', 'poireau125px.jpg' , '1', '4.82', '4.82');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (23, 23, 2, 'Pommes Canada Grise', 'Savourez ces pommes Canada. Vergers d''altitude à l''exploitation familiale. Barquette de 4 fruits soit 750 g environ. Origine / calibre * : 4 fruits - 750 g environ - France - Cal : 170/200 - Catégorie : 1.', 'pommeCanada.jpg', 'pommeCanada125px.jpg', '0.750', '4.04', '4.90');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (24, 24, 2, 'Pommes Chanteclerc', 'Cette pomme est excellente ! Sa chair fine et fondante dégage une saveur à la fois sucrée et acidulée, très aromatique. Elle a une forme légèrement aplatie et une robe très caractéristique, jaune or, un peu rugueuse et parsemée de lenticelles qui lui donne un aspect rustique. On la déguste d''octobre à juin. La pomme Chanteclerc est très polyvalente ! Elle se consomme en tarte, cuite au four ou pour accompagner tous vos plats. Elle est idéale pour la réalisation de vos tartes Tatin... Avis aux amateurs ! Origine / calibre * : 4 fruits - 850 g environ - France - Cal : 170/200.', 'pommeChanteclerc.jpg', 'pommeChanteclerc125px.jpg', '0.850', '4.67', '4.99');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (25, 25, 3, 'Pommes de terre blanches', 'Sa forme oblongue et régulière, sa taille moyenne, sa peau fine donne à cette pomme de terre blonde, variété à chair ferme une très belle allure, offrant une présentation impeccable à table. Ses atouts sont sa bonne productivité et ses excellentes qualités gustatives, particulièrement en friture, vapeur et salade. Origine / calibre * : 2,5 kg - France - Cal : 35+ - Catégorie 1', 'pommeDeTerre.jpg', 'pommeDeTerre125px.jpg' , '2.5', '4.99', '2.00');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (26, 26, 3, 'Pommes de terre rouges', 'Pommes de terre rouge de saison à la chair ferme, idéales pour préparer vos salades, pommes sautées ou pomme vapeur. Filet de 2,5 kg de pommes de terre rouge. Origine / calibre * : 2,5 kg - France - Cal 35+ - Catégorie : 1', 'pommeDeTerreRouge.jpg', 'pommeDeTerreRouge125px.jpg', '2.5', '4.99', '2.00');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (27, 27, 2, 'Pomme Golden', 'Chargée de symboles et de mythes, la pomme était déjà appréciée dans le monde antique. Aujourd''hui encore, c''est le fruit le plus consommé en France. La golden est une petite pomme jaune doré, présente toute l''année sur nos étals. Origine / calibre * : 2 Kg - France - Cal 85/115 - 95/115 - Catégorie : 2', 'pommeGolden.jpg', 'pommeGolden125px.jpg', '2', '3.99', '2.00');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (28, 28, 4, 'Salade Batavia', 'Cultivée selon une agriculture raisonnée, la récolte garantit un produit plus frais et plus qualitatif. Batavia en sachet ou en barquette, la pièce. Origine / calibre * : 1 pièce - France - Cal 350G - Catégorie : 1', 'saladeBatavia.jpg', 'saladeBatavia125px.jpg', '1', '1.68', '4.20');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (29, 29, 4, 'Salade coeur de romaine', 'Salade coeur de Romaine, salade connue pour son coeur ferme et aux feuilles craquantes. Sachet de 2 pièces. Origine / calibre * : 2 pièces - France - Catégorie : 1', 'saladeCoeurDeRomaine.jpg', 'saladeCoeurDeRomaine125px.jpg', '2', '4.40', '6.29');
INSERT INTO `article`(id, stock_id, categorie_id, nom, descriptif, image , imageMini, quantiteVendue, prix, prixKilo) VALUES (30, 30, 5, 'Thym sec', 'Le thym est une plante aromatique qui se reconnaît à ses petites feuilles gris-vert. Il provient de la région méditerranéenne. Il s''harmonise bien avec le romarin, la marjolaine, la sauge et la sarriette. Il est l''un des composants du bouquet garni et il aromatise très bien les vinaigres. Origine / calibre * : 30 G - France - Catégorie : 2', 'thym.jpg', 'thym125px.jpg', '0.030', '1.57', '52.33');
